import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/lib/auth";
import { useLocation } from "wouter";
import { Header } from "@/components/Header";
import { Footer } from "@/components/Footer";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Plus, Edit, Trash2, Ticket } from "lucide-react";

interface Coupon {
  id: string;
  code: string;
  discountType: "percentage" | "fixed";
  discountValue: string;
  isActive: boolean;
  expiresAt: string | null;
  usageLimit: number | null;
  usageCount: number;
}

const couponSchema = z.object({
  code: z
    .string()
    .min(3, "Code must be at least 3 characters")
    .max(50, "Code must be at most 50 characters")
    .regex(/^[A-Z0-9]+$/, "Code must be uppercase letters and numbers only"),
  discountType: z.enum(["percentage", "fixed"]),
  discountValue: z.coerce.number().min(0.01, "Discount must be greater than 0"),
  isActive: z.boolean(),
  expiresAt: z.string().optional(),
  usageLimit: z.coerce.number().min(1).optional(),
});

type CouponFormData = z.infer<typeof couponSchema>;

export default function AdminCoupons() {
  const { user } = useAuth();
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingCoupon, setEditingCoupon] = useState<Coupon | null>(null);

  const { data: coupons, isLoading } = useQuery<Coupon[]>({
    queryKey: ["/api/admin/coupons"],
    enabled: !!user?.isAdmin,
  });

  const form = useForm<CouponFormData>({
    resolver: zodResolver(couponSchema),
    defaultValues: {
      code: "",
      discountType: "percentage",
      discountValue: 0,
      isActive: true,
      expiresAt: undefined,
      usageLimit: undefined,
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: CouponFormData) => {
      return apiRequest("POST", "/api/admin/coupons", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/coupons"] });
      toast({
        title: "Coupon Created",
        description: "The coupon has been created successfully.",
      });
      setIsDialogOpen(false);
      form.reset();
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create coupon.",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: CouponFormData }) => {
      return apiRequest("PATCH", `/api/admin/coupons/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/coupons"] });
      toast({
        title: "Coupon Updated",
        description: "The coupon has been updated successfully.",
      });
      setIsDialogOpen(false);
      setEditingCoupon(null);
      form.reset();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update coupon.",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/admin/coupons/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/coupons"] });
      toast({
        title: "Coupon Deleted",
        description: "The coupon has been deleted successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete coupon.",
        variant: "destructive",
      });
    },
  });

  if (!user?.isAdmin) {
    return (
      <div className="min-h-screen bg-black flex items-center justify-center">
        <Card className="bg-zinc-900 border-yellow-500/20">
          <CardContent className="p-8 text-center">
            <h2 className="text-2xl font-bebas text-yellow-400 mb-4">ACCESS DENIED</h2>
            <p className="text-zinc-400">Administrator access required</p>
            <Button
              onClick={() => navigate("/")}
              className="mt-4"
              data-testid="button-home"
            >
              Return Home
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  const handleEdit = (coupon: Coupon) => {
    setEditingCoupon(coupon);
    form.reset({
      code: coupon.code,
      discountType: coupon.discountType,
      discountValue: parseFloat(coupon.discountValue),
      isActive: coupon.isActive,
      expiresAt: coupon.expiresAt
        ? new Date(coupon.expiresAt).toISOString().slice(0, 16)
        : undefined,
      usageLimit: coupon.usageLimit || undefined,
    });
    setIsDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setIsDialogOpen(false);
    setEditingCoupon(null);
    form.reset();
  };

  const onSubmit = (data: CouponFormData) => {
    if (editingCoupon) {
      updateMutation.mutate({ id: editingCoupon.id, data });
    } else {
      createMutation.mutate(data);
    }
  };

  const isExpired = (expiresAt: string | null) => {
    if (!expiresAt) return false;
    return new Date(expiresAt) < new Date();
  };

  const isLimitReached = (coupon: Coupon) => {
    if (!coupon.usageLimit) return false;
    return coupon.usageCount >= coupon.usageLimit;
  };

  return (
    <div className="min-h-screen bg-black text-white">
      <Header />

      <main className="max-w-7xl mx-auto px-4 py-8 mt-20">
        <div className="mb-8 flex items-center justify-between">
          <div>
            <h1 className="text-5xl font-bebas text-yellow-400 mb-2">
              COUPON MANAGEMENT
            </h1>
            <p className="text-zinc-400">Create and manage discount coupons</p>
          </div>
          <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
            <DialogTrigger asChild>
              <Button
                className="bg-yellow-400 text-black hover:bg-yellow-500"
                data-testid="button-create-coupon"
              >
                <Plus className="w-4 h-4 mr-2" />
                Create Coupon
              </Button>
            </DialogTrigger>
            <DialogContent className="bg-zinc-900 border-yellow-500/20 text-white">
              <DialogHeader>
                <DialogTitle className="text-2xl font-bebas text-yellow-400">
                  {editingCoupon ? "EDIT COUPON" : "CREATE COUPON"}
                </DialogTitle>
              </DialogHeader>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="code"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Coupon Code</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            className="bg-black border-zinc-700 uppercase"
                            placeholder="e.g., WELCOME10"
                            maxLength={50}
                            onChange={(e) =>
                              field.onChange(e.target.value.toUpperCase())
                            }
                            data-testid="input-code"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="discountType"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Discount Type</FormLabel>
                        <Select
                          onValueChange={field.onChange}
                          defaultValue={field.value}
                        >
                          <FormControl>
                            <SelectTrigger
                              className="bg-black border-zinc-700"
                              data-testid="select-type"
                            >
                              <SelectValue placeholder="Select type" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="percentage">Percentage (%)</SelectItem>
                            <SelectItem value="fixed">Fixed Amount (RM)</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="discountValue"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Discount Value</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="number"
                            step="0.01"
                            className="bg-black border-zinc-700"
                            placeholder="e.g., 10"
                            data-testid="input-value"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="expiresAt"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Expiry Date (Optional)</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="datetime-local"
                            className="bg-black border-zinc-700"
                            data-testid="input-expiry"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="usageLimit"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Usage Limit (Optional)</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="number"
                            className="bg-black border-zinc-700"
                            placeholder="e.g., 100"
                            data-testid="input-limit"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="isActive"
                    render={({ field }) => (
                      <FormItem className="flex items-center gap-2">
                        <FormControl>
                          <input
                            type="checkbox"
                            checked={field.value}
                            onChange={field.onChange}
                            className="w-4 h-4"
                            data-testid="input-active"
                          />
                        </FormControl>
                        <FormLabel className="mb-0">Active</FormLabel>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <div className="flex gap-2 pt-4">
                    <Button
                      type="submit"
                      className="flex-1 bg-yellow-400 text-black hover:bg-yellow-500"
                      disabled={createMutation.isPending || updateMutation.isPending}
                      data-testid="button-submit-coupon"
                    >
                      {createMutation.isPending || updateMutation.isPending
                        ? "Saving..."
                        : editingCoupon
                        ? "Update"
                        : "Create"}
                    </Button>
                    <Button
                      type="button"
                      variant="outline"
                      onClick={handleCloseDialog}
                      data-testid="button-cancel"
                    >
                      Cancel
                    </Button>
                  </div>
                </form>
              </Form>
            </DialogContent>
          </Dialog>
        </div>

        <Card className="bg-zinc-900 border-yellow-500/20">
          <CardContent className="p-6">
            {isLoading ? (
              <div className="text-center py-12">
                <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-yellow-400" />
              </div>
            ) : coupons && coupons.length > 0 ? (
              <div className="space-y-4">
                {coupons.map((coupon) => {
                  const expired = isExpired(coupon.expiresAt);
                  const limitReached = isLimitReached(coupon);
                  const inactive = !coupon.isActive;
                  const disabled = expired || limitReached || inactive;

                  return (
                    <Card
                      key={coupon.id}
                      className="bg-black border-zinc-800 hover-elevate"
                    >
                      <CardContent className="p-6">
                        <div className="flex flex-col lg:flex-row gap-4 lg:items-center lg:justify-between">
                          <div className="space-y-2 flex-1">
                            <div className="flex items-center gap-2 flex-wrap">
                              <h3 className="text-2xl font-mono font-bold text-yellow-400">
                                {coupon.code}
                              </h3>
                              {coupon.isActive && !expired && !limitReached && (
                                <Badge className="bg-green-500/20 text-green-400 border-green-500/50">
                                  ACTIVE
                                </Badge>
                              )}
                              {inactive && (
                                <Badge className="bg-zinc-500/20 text-zinc-400 border-zinc-500/50">
                                  INACTIVE
                                </Badge>
                              )}
                              {expired && (
                                <Badge className="bg-red-500/20 text-red-400 border-red-500/50">
                                  EXPIRED
                                </Badge>
                              )}
                              {limitReached && (
                                <Badge className="bg-red-500/20 text-red-400 border-red-500/50">
                                  LIMIT REACHED
                                </Badge>
                              )}
                            </div>
                            <div className="space-y-1 text-sm text-zinc-400">
                              <p>
                                Discount:{" "}
                                <span className="text-white font-semibold">
                                  {coupon.discountType === "percentage"
                                    ? `${coupon.discountValue}%`
                                    : `RM ${coupon.discountValue}`}
                                </span>
                              </p>
                              {coupon.expiresAt && (
                                <p>
                                  Expires:{" "}
                                  <span className="text-white">
                                    {new Date(coupon.expiresAt).toLocaleString()}
                                  </span>
                                </p>
                              )}
                              {coupon.usageLimit && (
                                <p>
                                  Usage:{" "}
                                  <span className="text-white">
                                    {coupon.usageCount} / {coupon.usageLimit}
                                  </span>
                                </p>
                              )}
                              {!coupon.usageLimit && coupon.usageCount > 0 && (
                                <p>
                                  Used:{" "}
                                  <span className="text-white">
                                    {coupon.usageCount} times
                                  </span>
                                </p>
                              )}
                            </div>
                          </div>
                          <div className="flex gap-2">
                            <Button
                              size="sm"
                              variant="outline"
                              className="border-yellow-500/50 text-yellow-400 hover:bg-yellow-500/10"
                              onClick={() => handleEdit(coupon)}
                              data-testid={`button-edit-${coupon.id}`}
                            >
                              <Edit className="w-4 h-4 mr-1" />
                              Edit
                            </Button>
                            <Button
                              size="sm"
                              variant="outline"
                              className="border-red-500/50 text-red-400 hover:bg-red-500/10"
                              onClick={() => {
                                if (
                                  confirm(
                                    "Are you sure you want to delete this coupon?"
                                  )
                                ) {
                                  deleteMutation.mutate(coupon.id);
                                }
                              }}
                              data-testid={`button-delete-${coupon.id}`}
                            >
                              <Trash2 className="w-4 h-4 mr-1" />
                              Delete
                            </Button>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  );
                })}
              </div>
            ) : (
              <div className="text-center py-12">
                <Ticket className="w-16 h-16 text-zinc-700 mx-auto mb-4" />
                <p className="text-zinc-400">No coupons found</p>
              </div>
            )}
          </CardContent>
        </Card>
      </main>

      <Footer />
    </div>
  );
}

client/src/pages/AdminLogin.tsx.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { useAuth } from "@/lib/auth";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Shield } from "lucide-react";

const loginSchema = z.object({
  username: z.string().min(3, "Username must be at least 3 characters"),
  password: z.string().min(6, "Password must be at least 6 characters"),
});

type LoginFormData = z.infer<typeof loginSchema>;

export default function AdminLogin() {
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const { user, refreshUser } = useAuth();

  const form = useForm<LoginFormData>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: "",
      password: "",
    },
  });

  const loginMutation = useMutation({
    mutationFn: async (data: LoginFormData) => {
      const res = await apiRequest("POST", "/api/admin/login", data);
      return res.json();
    },
    onSuccess: async () => {
      await refreshUser();
      toast({
        title: "Login Successful",
        description: "Welcome back, admin!",
      });
      navigate("/admin/orders");
    },
    onError: (error: any) => {
      toast({
        title: "Login Failed",
        description: error.message || "Invalid username or password",
        variant: "destructive",
      });
    },
  });

  if (user?.isAdmin) {
    navigate("/admin/orders");
    return null;
  }

  const onSubmit = (data: LoginFormData) => {
    loginMutation.mutate(data);
  };

  return (
    <div className="min-h-screen bg-black flex items-center justify-center p-4">
      <Card className="w-full max-w-md bg-zinc-900 border-yellow-500/20">
        <CardHeader className="text-center">
          <div className="flex justify-center mb-4">
            <div className="w-16 h-16 rounded-full bg-yellow-400/10 flex items-center justify-center">
              <Shield className="w-8 h-8 text-yellow-400" />
            </div>
          </div>
          <CardTitle className="text-3xl font-bebas text-yellow-400">
            ADMIN LOGIN
          </CardTitle>
          <p className="text-zinc-400 text-sm">
            Administrator access only
          </p>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="username"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Username</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        className="bg-black border-zinc-700"
                        placeholder="Enter your username"
                        autoComplete="username"
                        data-testid="input-username"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Password</FormLabel>
                    <FormControl>
                      <Input
                        {...field}
                        type="password"
                        className="bg-black border-zinc-700"
                        placeholder="Enter your password"
                        autoComplete="current-password"
                        data-testid="input-password"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <Button
                type="submit"
                className="w-full bg-yellow-400 text-black hover:bg-yellow-500 font-bold"
                disabled={loginMutation.isPending}
                data-testid="button-login"
              >
                {loginMutation.isPending ? "Logging in..." : "LOGIN"}
              </Button>
            </form>
          </Form>

          <div className="mt-6 text-center">
            <Button
              variant="ghost"
              onClick={() => navigate("/")}
              className="text-zinc-400 hover:text-white"
              data-testid="button-back-home"
            >
              â† Back to Home
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/AdminOrders.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/lib/auth";
import { useLocation } from "wouter";
import { Header } from "@/components/Header";
import { Footer } from "@/components/Footer";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { Search, Package, DollarSign, Users, CheckCircle } from "lucide-react";

type OrderStatus = "created" | "paid" | "fulfilled" | "failed";

interface Order {
  id: string;
  userId: string;
  userName: string;
  userEmail: string;
  totalAmount: string;
  currency: string;
  status: OrderStatus;
  paymentMethod: string;
  createdAt: string;
  orderItems: Array<{
    packageName: string;
    quantity: number;
    price: string;
  }>;
  redemptionCodes: Array<{
    code: string;
  }>;
}

export default function AdminOrders() {
  const { user } = useAuth();
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const [statusFilter, setStatusFilter] = useState<string>("all");
  const [searchTerm, setSearchTerm] = useState("");

  const { data: orders, isLoading } = useQuery<Order[]>({
    queryKey: ["/api/admin/orders"],
    enabled: !!user?.isAdmin,
  });

  const updateStatusMutation = useMutation({
    mutationFn: async ({ orderId, status }: { orderId: string; status: OrderStatus }) => {
      return apiRequest("PATCH", `/api/admin/orders/${orderId}/status`, { status });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/orders"] });
      toast({
        title: "Status Updated",
        description: "Order status has been updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update order status.",
        variant: "destructive",
      });
    },
  });

  if (!user?.isAdmin) {
    return (
      <div className="min-h-screen bg-black flex items-center justify-center">
        <Card className="bg-zinc-900 border-yellow-500/20">
          <CardContent className="p-8 text-center">
            <h2 className="text-2xl font-bebas text-yellow-400 mb-4">ACCESS DENIED</h2>
            <p className="text-zinc-400">Administrator access required</p>
            <Button
              onClick={() => navigate("/")}
              className="mt-4"
              data-testid="button-home"
            >
              Return Home
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  const filteredOrders = orders?.filter((order) => {
    const matchesStatus = statusFilter === "all" || order.status === statusFilter;
    const matchesSearch =
      (order.userEmail?.toLowerCase() || "").includes(searchTerm.toLowerCase()) ||
      (order.userName?.toLowerCase() || "").includes(searchTerm.toLowerCase()) ||
      (order.id?.toLowerCase() || "").includes(searchTerm.toLowerCase());
    return matchesStatus && matchesSearch;
  });

  const stats = {
    total: orders?.length || 0,
    paid: orders?.filter((o) => o.status === "paid" || o.status === "fulfilled").length || 0,
    revenue: orders
      ?.filter((o) => o.status === "paid" || o.status === "fulfilled")
      .reduce((sum, o) => sum + parseFloat(o.totalAmount), 0)
      .toFixed(2) || "0.00",
  };

  const getStatusColor = (status: OrderStatus) => {
    switch (status) {
      case "fulfilled":
        return "bg-green-500/20 text-green-400 border-green-500/50";
      case "paid":
        return "bg-blue-500/20 text-blue-400 border-blue-500/50";
      case "created":
        return "bg-yellow-500/20 text-yellow-400 border-yellow-500/50";
      case "failed":
        return "bg-red-500/20 text-red-400 border-red-500/50";
      default:
        return "bg-zinc-500/20 text-zinc-400 border-zinc-500/50";
    }
  };

  return (
    <div className="min-h-screen bg-black text-white">
      <Header />

      <main className="max-w-7xl mx-auto px-4 py-8 mt-20">
        <div className="mb-8">
          <h1 className="text-5xl font-bebas text-yellow-400 mb-2">
            ADMIN DASHBOARD
          </h1>
          <p className="text-zinc-400">Manage orders, packages, and coupons</p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
          <Card className="bg-zinc-900 border-yellow-500/20">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-zinc-400 text-sm">Total Orders</p>
                  <p className="text-3xl font-bold text-yellow-400">{stats.total}</p>
                </div>
                <Package className="w-8 h-8 text-yellow-400/50" />
              </div>
            </CardContent>
          </Card>

          <Card className="bg-zinc-900 border-yellow-500/20">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-zinc-400 text-sm">Paid Orders</p>
                  <p className="text-3xl font-bold text-green-400">{stats.paid}</p>
                </div>
                <CheckCircle className="w-8 h-8 text-green-400/50" />
              </div>
            </CardContent>
          </Card>

          <Card className="bg-zinc-900 border-yellow-500/20">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-zinc-400 text-sm">Total Revenue</p>
                  <p className="text-3xl font-bold text-yellow-400">RM{stats.revenue}</p>
                </div>
                <DollarSign className="w-8 h-8 text-yellow-400/50" />
              </div>
            </CardContent>
          </Card>
        </div>

        <Card className="bg-zinc-900 border-yellow-500/20 mb-4">
          <CardHeader>
            <CardTitle className="text-2xl font-bebas text-yellow-400">
              ORDER MANAGEMENT
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex flex-col md:flex-row gap-4 mb-6">
              <div className="flex-1 relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-zinc-400 w-4 h-4" />
                <Input
                  placeholder="Search by email, name, or order ID..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 bg-black border-zinc-700"
                  data-testid="input-search"
                />
              </div>
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-full md:w-[200px] bg-black border-zinc-700" data-testid="select-status">
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Orders</SelectItem>
                  <SelectItem value="created">Created</SelectItem>
                  <SelectItem value="paid">Paid</SelectItem>
                  <SelectItem value="fulfilled">Fulfilled</SelectItem>
                  <SelectItem value="failed">Failed</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {isLoading ? (
              <div className="text-center py-12">
                <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-yellow-400" />
              </div>
            ) : filteredOrders && filteredOrders.length > 0 ? (
              <div className="space-y-4">
                {filteredOrders.map((order) => (
                  <Card
                    key={order.id}
                    className="bg-black border-zinc-800 hover-elevate"
                  >
                    <CardContent className="p-6">
                      <div className="flex flex-col lg:flex-row gap-4 lg:items-center lg:justify-between">
                        <div className="space-y-2 flex-1">
                          <div className="flex items-center gap-2 flex-wrap">
                            <Badge className={getStatusColor(order.status)}>
                              {order.status.toUpperCase()}
                            </Badge>
                            <Badge variant="outline" className="border-yellow-500/50 text-yellow-400">
                              {order.paymentMethod}
                            </Badge>
                          </div>
                          <p className="text-sm text-zinc-400" data-testid={`text-order-id-${order.id}`}>
                            Order ID: {order.id}
                          </p>
                          <div className="flex items-center gap-2 text-sm">
                            <Users className="w-4 h-4 text-zinc-400" />
                            <span className="text-white">{order.userName}</span>
                            <span className="text-zinc-600">â€¢</span>
                            <span className="text-zinc-400">{order.userEmail}</span>
                          </div>
                          <div className="text-sm text-zinc-400">
                            {new Date(order.createdAt).toLocaleString()}
                          </div>
                          <div className="space-y-1">
                            {order.orderItems?.map((item, idx) => (
                              <p key={idx} className="text-sm text-zinc-300">
                                {item.quantity}x {item.packageName} - {order.currency}{" "}
                                {item.price}
                              </p>
                            ))}
                          </div>
                          {order.redemptionCodes && order.redemptionCodes.length > 0 && (
                            <details className="text-sm">
                              <summary className="cursor-pointer text-yellow-400 hover:text-yellow-300">
                                View Codes ({order.redemptionCodes.length})
                              </summary>
                              <div className="mt-2 space-y-1 pl-4">
                                {order.redemptionCodes.map((code, idx) => (
                                  <p key={idx} className="font-mono text-zinc-300">
                                    {code.code}
                                  </p>
                                ))}
                              </div>
                            </details>
                          )}
                        </div>
                        <div className="flex flex-col items-end gap-2">
                          <p className="text-2xl font-bold text-yellow-400">
                            {order.currency} {order.totalAmount}
                          </p>
                          <Select
                            value={order.status}
                            onValueChange={(value) =>
                              updateStatusMutation.mutate({
                                orderId: order.id,
                                status: value as OrderStatus,
                              })
                            }
                          >
                            <SelectTrigger
                              className="w-[150px] bg-zinc-800 border-zinc-700"
                              data-testid={`select-status-${order.id}`}
                            >
                              <SelectValue />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="created">Created</SelectItem>
                              <SelectItem value="paid">Paid</SelectItem>
                              <SelectItem value="fulfilled">Fulfilled</SelectItem>
                              <SelectItem value="failed">Failed</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <Package className="w-16 h-16 text-zinc-700 mx-auto mb-4" />
                <p className="text-zinc-400">No orders found</p>
              </div>
            )}
          </CardContent>
        </Card>
      </main>

      <Footer />
    </div>
  );
}

client/src/pages/AdminPackages.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/lib/auth";
import { useLocation } from "wouter";
import { Header } from "@/components/Header";
import { Footer } from "@/components/Footer";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Plus, Edit, Trash2, Package } from "lucide-react";

interface Package {
  id: string;
  name: string;
  aecoinAmount: number;
  priceRM: string;
  description: string;
  isPopular: boolean;
}

const packageSchema = z.object({
  name: z.string().min(1, "Name is required"),
  aecoinAmount: z.coerce.number().min(1, "Amount must be at least 1"),
  priceRM: z.coerce.number().min(1, "Price must be at least RM1"),
  description: z.string().min(1, "Description is required"),
  isPopular: z.boolean(),
});

type PackageFormData = z.infer<typeof packageSchema>;

export default function AdminPackages() {
  const { user } = useAuth();
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingPackage, setEditingPackage] = useState<Package | null>(null);

  const { data: packages, isLoading } = useQuery<Package[]>({
    queryKey: ["/api/packages"],
  });

  const form = useForm<PackageFormData>({
    resolver: zodResolver(packageSchema),
    defaultValues: {
      name: "",
      aecoinAmount: 0,
      priceRM: 0,
      description: "",
      isPopular: false,
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: PackageFormData) => {
      return apiRequest("POST", "/api/admin/packages", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/packages"] });
      toast({
        title: "Package Created",
        description: "The package has been created successfully.",
      });
      setIsDialogOpen(false);
      form.reset();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create package.",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: PackageFormData }) => {
      return apiRequest("PATCH", `/api/admin/packages/${id}`, data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/packages"] });
      toast({
        title: "Package Updated",
        description: "The package has been updated successfully.",
      });
      setIsDialogOpen(false);
      setEditingPackage(null);
      form.reset();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update package.",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async (id: string) => {
      return apiRequest("DELETE", `/api/admin/packages/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/packages"] });
      toast({
        title: "Package Deleted",
        description: "The package has been deleted successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete package.",
        variant: "destructive",
      });
    },
  });

  if (!user?.isAdmin) {
    return (
      <div className="min-h-screen bg-black flex items-center justify-center">
        <Card className="bg-zinc-900 border-yellow-500/20">
          <CardContent className="p-8 text-center">
            <h2 className="text-2xl font-bebas text-yellow-400 mb-4">ACCESS DENIED</h2>
            <p className="text-zinc-400">Administrator access required</p>
            <Button
              onClick={() => navigate("/")}
              className="mt-4"
              data-testid="button-home"
            >
              Return Home
            </Button>
          </CardContent>
        </Card>
      </div>
    );
  }

  const handleEdit = (pkg: Package) => {
    setEditingPackage(pkg);
    form.reset({
      name: pkg.name,
      aecoinAmount: pkg.aecoinAmount,
      priceRM: parseFloat(pkg.priceRM),
      description: pkg.description,
      isPopular: pkg.isPopular,
    });
    setIsDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setIsDialogOpen(false);
    setEditingPackage(null);
    form.reset();
  };

  const onSubmit = (data: PackageFormData) => {
    if (editingPackage) {
      updateMutation.mutate({ id: editingPackage.id, data });
    } else {
      createMutation.mutate(data);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white">
      <Header />

      <main className="max-w-7xl mx-auto px-4 py-8 mt-20">
        <div className="mb-8 flex items-center justify-between">
          <div>
            <h1 className="text-5xl font-bebas text-yellow-400 mb-2">
              PACKAGE MANAGEMENT
            </h1>
            <p className="text-zinc-400">Create and manage AECOIN packages</p>
          </div>
          <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
            <DialogTrigger asChild>
              <Button
                className="bg-yellow-400 text-black hover:bg-yellow-500"
                data-testid="button-create-package"
              >
                <Plus className="w-4 h-4 mr-2" />
                Create Package
              </Button>
            </DialogTrigger>
            <DialogContent className="bg-zinc-900 border-yellow-500/20 text-white">
              <DialogHeader>
                <DialogTitle className="text-2xl font-bebas text-yellow-400">
                  {editingPackage ? "EDIT PACKAGE" : "CREATE PACKAGE"}
                </DialogTitle>
              </DialogHeader>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Package Name</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            className="bg-black border-zinc-700"
                            placeholder="e.g., Starter Pack"
                            data-testid="input-name"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="aecoinAmount"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>AECOIN Amount</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="number"
                            className="bg-black border-zinc-700"
                            placeholder="e.g., 500"
                            data-testid="input-aecoin"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="priceRM"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Price (RM)</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="number"
                            step="0.01"
                            className="bg-black border-zinc-700"
                            placeholder="e.g., 50.00"
                            data-testid="input-price"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Description</FormLabel>
                        <FormControl>
                          <Textarea
                            {...field}
                            className="bg-black border-zinc-700"
                            placeholder="Package description..."
                            rows={3}
                            data-testid="input-description"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="isPopular"
                    render={({ field }) => (
                      <FormItem className="flex items-center gap-2">
                        <FormControl>
                          <input
                            type="checkbox"
                            checked={field.value}
                            onChange={field.onChange}
                            className="w-4 h-4"
                            data-testid="input-popular"
                          />
                        </FormControl>
                        <FormLabel className="mb-0">Mark as Popular</FormLabel>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <div className="flex gap-2 pt-4">
                    <Button
                      type="submit"
                      className="flex-1 bg-yellow-400 text-black hover:bg-yellow-500"
                      disabled={createMutation.isPending || updateMutation.isPending}
                      data-testid="button-submit-package"
                    >
                      {createMutation.isPending || updateMutation.isPending
                        ? "Saving..."
                        : editingPackage
                        ? "Update"
                        : "Create"}
                    </Button>
                    <Button
                      type="button"
                      variant="outline"
                      onClick={handleCloseDialog}
                      data-testid="button-cancel"
                    >
                      Cancel
                    </Button>
                  </div>
                </form>
              </Form>
            </DialogContent>
          </Dialog>
        </div>

        <Card className="bg-zinc-900 border-yellow-500/20">
          <CardContent className="p-6">
            {isLoading ? (
              <div className="text-center py-12">
                <div className="inline-block animate-spin rounded-full h-8 w-8 border-b-2 border-yellow-400" />
              </div>
            ) : packages && packages.length > 0 ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {packages.map((pkg) => (
                  <Card
                    key={pkg.id}
                    className="bg-black border-zinc-800 hover-elevate"
                  >
                    <CardContent className="p-6">
                      <div className="space-y-4">
                        <div className="flex items-start justify-between">
                          <div>
                            <h3 className="text-xl font-bebas text-yellow-400">
                              {pkg.name}
                            </h3>
                            {pkg.isPopular && (
                              <Badge className="bg-yellow-500/20 text-yellow-400 border-yellow-500/50 mt-1">
                                POPULAR
                              </Badge>
                            )}
                          </div>
                        </div>
                        <div className="space-y-2">
                          <p className="text-3xl font-bold text-white">
                            {pkg.aecoinAmount.toLocaleString()}
                            <span className="text-sm text-yellow-400 ml-1">AECOIN</span>
                          </p>
                          <p className="text-2xl font-bold text-yellow-400">
                            RM {pkg.priceRM}
                          </p>
                        </div>
                        <p className="text-sm text-zinc-400">{pkg.description}</p>
                        <div className="flex gap-2 pt-2">
                          <Button
                            size="sm"
                            variant="outline"
                            className="flex-1 border-yellow-500/50 text-yellow-400 hover:bg-yellow-500/10"
                            onClick={() => handleEdit(pkg)}
                            data-testid={`button-edit-${pkg.id}`}
                          >
                            <Edit className="w-4 h-4 mr-1" />
                            Edit
                          </Button>
                          <Button
                            size="sm"
                            variant="outline"
                            className="flex-1 border-red-500/50 text-red-400 hover:bg-red-500/10"
                            onClick={() => {
                              if (
                                confirm(
                                  "Are you sure you want to delete this package?"
                                )
                              ) {
                                deleteMutation.mutate(pkg.id);
                              }
                            }}
                            data-testid={`button-delete-${pkg.id}`}
                          >
                            <Trash2 className="w-4 h-4 mr-1" />
                            Delete
                          </Button>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            ) : (
              <div className="text-center py-12">
                <Package className="w-16 h-16 text-zinc-700 mx-auto mb-4" />
                <p className="text-zinc-400">No packages found</p>
              </div>
            )}
          </CardContent>
        </Card>
      </main>

      <Footer />
    </div>
  );
}

client/src/pages/Checkout.tsx.content:
import { useState, useEffect } from "react";
import { useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { ArrowLeft, Wallet, Tag, Loader2, CreditCard } from "lucide-react";
import { useAuth } from "@/lib/auth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import type { CartItem, Package, Coupon } from "@shared/schema";

interface CartItemWithPackage extends CartItem {
  package: Package;
}

export default function Checkout() {
  const [, navigate] = useLocation();
  const { user } = useAuth();
  const { toast } = useToast();

  const [couponCode, setCouponCode] = useState("");
  const [appliedCoupon, setAppliedCoupon] = useState<Coupon | null>(null);
  const [paymentMethod, setPaymentMethod] = useState<"toyyibpay" | "billplz">("toyyibpay");

  // Billing information
  const [billingInfo, setBillingInfo] = useState({
    fullName: "",
    email: user?.email || "",
    phoneNumber: "",
  });

  // Pre-fill email when user data loads
  useEffect(() => {
    if (user?.email && !billingInfo.email) {
      setBillingInfo(prev => ({ ...prev, email: user.email }));
    }
  }, [user]);

  // Check for error query parameter and redirect BEFORE anything else
  const params = new URLSearchParams(window.location.search);
  const error = params.get('error');

  if (error === 'payment_failed') {
    navigate('/payment/failed');
    return null;
  } else if (error === 'payment_cancelled') {
    navigate('/payment/cancelled');
    return null;
  }

  // Fetch cart items (only if user is authenticated)
  const { data: cartItems = [], isLoading } = useQuery<CartItemWithPackage[]>({
    queryKey: ["/api/cart"],
    enabled: !!user,
  });

  // Validate coupon mutation
  const validateCoupon = useMutation({
    mutationFn: async (code: string) => {
      const response = await apiRequest("GET", `/api/coupons/${code}?subtotal=${subtotal}`);
      return await response.json();
    },
    onSuccess: (data: Coupon) => {
      // Check minimum purchase on frontend as well
      if (data.minPurchase && parseFloat(data.minPurchase) > subtotal) {
        toast({
          title: "Coupon Not Applicable",
          description: `Minimum purchase of RM${data.minPurchase} required.`,
          variant: "destructive",
        });
        return;
      }

      setAppliedCoupon(data);
      const discountText = data.discountType === 'percentage' 
        ? `${data.discountValue}% discount` 
        : `RM${data.discountValue} discount`;
      toast({
        title: "Coupon Applied!",
        description: `${discountText} applied successfully.`,
      });
    },
    onError: (error: any) => {
      const message = error.message || "The coupon code you entered is invalid or expired.";
      toast({
        title: "Invalid Coupon",
        description: message,
        variant: "destructive",
      });
    },
  });

  // Calculate totals
  const subtotal = cartItems.reduce((sum, item) => {
    return sum + parseFloat(item.package.price) * item.quantity;
  }, 0);

  const discountAmount = appliedCoupon 
    ? appliedCoupon.discountType === 'percentage'
      ? (subtotal * parseFloat(appliedCoupon.discountValue)) / 100
      : parseFloat(appliedCoupon.discountValue)
    : 0;

  const total = Math.max(0, subtotal - discountAmount);

  const handleApplyCoupon = () => {
    if (couponCode.trim()) {
      validateCoupon.mutate(couponCode.trim().toUpperCase());
    }
  };

  const handleRemoveCoupon = () => {
    setAppliedCoupon(null);
    setCouponCode("");
  };


  const handlePaymentSuccess = () => {
    // Clear cart and redirect to orders page
    queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
    navigate("/orders");
  };

  const [isProcessingToyyibPay, setIsProcessingToyyibPay] = useState(false);
  const [isProcessingBillplz, setIsProcessingBillplz] = useState(false);

  const handleCheckout = async () => {
    if (cartItems.length === 0) {
      toast({
        title: "Cart is Empty",
        description: "Please add items to your cart before checkout.",
        variant: "destructive",
      });
      return;
    }

    // Validate billing information
    if (!billingInfo.fullName.trim()) {
      toast({
        title: "Missing Information",
        description: "Please enter your full name.",
        variant: "destructive",
      });
      return;
    }

    if (!billingInfo.email.trim()) {
      toast({
        title: "Missing Information",
        description: "Please enter your email address.",
        variant: "destructive",
      });
      return;
    }

    if (paymentMethod === "toyyibpay") {
      setIsProcessingToyyibPay(true);

      console.log("ToyyibPay: Sending billing info:", billingInfo);

      try {
        const response = await apiRequest("POST", "/api/create-toyyibpay-bill", {
          couponCode: appliedCoupon?.code || "",
          billingInfo,
        });

        const data = await response.json();

        if (data.paymentUrl) {
          window.location.href = data.paymentUrl;
        } else {
          toast({
            title: "Error",
            description: "Failed to create payment. Please try again.",
            variant: "destructive",
          });
        }
      } catch (error: any) {
        toast({
          title: "Payment Error",
          description: error.message || "Failed to initialize ToyyibPay payment.",
          variant: "destructive",
        });
      } finally {
        setIsProcessingToyyibPay(false);
      }
    } else if (paymentMethod === "billplz") {
      setIsProcessingBillplz(true);

      console.log("Billplz: Sending billing info:", billingInfo);

      try {
        const response = await apiRequest("POST", "/api/create-billplz-bill", {
          couponCode: appliedCoupon?.code || "",
          billingInfo,
        });

        const data = await response.json();

        if (data.paymentUrl) {
          window.location.href = data.paymentUrl;
        } else {
          toast({
            title: "Error",
            description: "Failed to create payment. Please try again.",
            variant: "destructive",
          });
        }
      } catch (error: any) {
        toast({
          title: "Payment Error",
          description: error.message || "Failed to initialize Billplz payment.",
          variant: "destructive",
        });
      } finally {
        setIsProcessingBillplz(false);
      }
    }
  };

  if (!user) {
    navigate("/");
    return null;
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-[#000000] flex items-center justify-center">
        <Loader2 className="w-8 h-8 text-neon-yellow animate-spin" />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-[#000000] pb-12">
      {/* Header */}
      <div className="bg-[#000000] border-b border-white/10 py-6">
        <div className="container mx-auto px-4">
          <Button
            variant="ghost"
            onClick={() => navigate("/")}
            className="text-white hover:text-neon-yellow mb-4"
            data-testid="button-back-to-shop"
          >
            <ArrowLeft className="w-4 h-4 mr-2" />
            Back to Shop
          </Button>
          <h1 className="text-4xl font-bebas text-white uppercase tracking-wider">
            Checkout
          </h1>
        </div>
      </div>

      <div className="container mx-auto px-4 mt-8">
        <div className="grid lg:grid-cols-3 gap-8">
          {/* Left Column - Payment Details */}
          <div className="lg:col-span-2 space-y-6">
            {/* Billing Information */}
            <Card className="bg-gradient-to-br from-[#1a1a1a] to-[#000000] border-2 border-white/10 rounded-3xl">
              <CardHeader>
                <CardTitle className="text-2xl font-bebas text-white uppercase tracking-wider">
                  Billing Information
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <label className="block text-sm font-rajdhani font-semibold text-white mb-2">
                    Full Name *
                  </label>
                  <Input
                    type="text"
                    value={billingInfo.fullName}
                    onChange={(e) => setBillingInfo(prev => ({ ...prev, fullName: e.target.value }))}
                    placeholder="Enter your full name"
                    className="bg-[#000000]/50 border-white/20 text-white placeholder:text-gray-500"
                    required
                    data-testid="input-billing-name"
                  />
                </div>

                <div>
                  <label className="block text-sm font-rajdhani font-semibold text-white mb-2">
                    Email Address *
                  </label>
                  <Input
                    type="email"
                    value={billingInfo.email}
                    onChange={(e) => setBillingInfo(prev => ({ ...prev, email: e.target.value }))}
                    placeholder="your@email.com"
                    className="bg-[#000000]/50 border-white/20 text-white placeholder:text-gray-500"
                    required
                    data-testid="input-billing-email"
                  />
                </div>

                <div>
                  <label className="block text-sm font-rajdhani font-semibold text-white mb-2">
                    Phone Number (Optional)
                  </label>
                  <Input
                    type="tel"
                    value={billingInfo.phoneNumber}
                    onChange={(e) => setBillingInfo(prev => ({ ...prev, phoneNumber: e.target.value }))}
                    placeholder="+60123456789"
                    className="bg-[#000000]/50 border-white/20 text-white placeholder:text-gray-500"
                    data-testid="input-billing-phone"
                  />
                  <p className="text-xs text-gray-400 mt-1">
                    Malaysian format: +60123456789
                  </p>
                </div>
              </CardContent>
            </Card>

            {/* Payment Method */}
            <Card className="bg-gradient-to-br from-[#1a1a1a] to-[#000000] border-2 border-white/10 rounded-3xl">
              <CardHeader>
                <CardTitle className="text-2xl font-bebas text-white uppercase tracking-wider">
                  Payment Method
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <button
                    onClick={() => setPaymentMethod("toyyibpay")}
                    className={`p-6 rounded-2xl border-2 transition-all ${
                      paymentMethod === "toyyibpay"
                        ? "border-neon-yellow bg-neon-yellow/10"
                        : "border-white/10 hover:border-white/20"
                    }`}
                    data-testid="button-payment-toyyibpay"
                  >
                    <Wallet className="w-12 h-12 mx-auto mb-3 text-neon-yellow" />
                    <p className="font-rajdhani font-bold text-white text-center">
                      ToyyibPay
                    </p>
                    <p className="text-xs text-gray-400 text-center mt-1">
                      FPX, Online Banking
                    </p>
                  </button>

                  <button
                    onClick={() => setPaymentMethod("billplz")}
                    className={`p-6 rounded-2xl border-2 transition-all ${
                      paymentMethod === "billplz"
                        ? "border-neon-yellow bg-neon-yellow/10"
                        : "border-white/10 hover:border-white/20"
                    }`}
                    data-testid="button-payment-billplz"
                  >
                    <Wallet className="w-12 h-12 mx-auto mb-3 text-neon-yellow" />
                    <p className="font-rajdhani font-bold text-white text-center">
                      Billplz
                    </p>
                    <p className="text-xs text-gray-400 text-center mt-1">
                      FPX, Cards, E-Wallets
                    </p>
                  </button>
                </div>
              </CardContent>
            </Card>

            {/* Payment Form */}
            <Card className="bg-gradient-to-br from-[#1a1a1a] to-[#000000] border-2 border-neon-yellow/30 rounded-3xl">
              <CardHeader>
                <CardTitle className="text-2xl font-bebas text-white uppercase tracking-wider">
                  Payment Details
                </CardTitle>
              </CardHeader>
              <CardContent>
                {paymentMethod === "toyyibpay" ? (
                  <div className="space-y-4">
                    <p className="text-gray-400 text-sm text-center py-8">
                      You will be redirected to ToyyibPay to complete your payment
                    </p>
                    <Button
                      onClick={handleCheckout}
                      disabled={cartItems.length === 0 || isProcessingToyyibPay}
                      className="w-full bg-neon-yellow hover:bg-neon-yellow/90 text-black font-bold uppercase text-base h-12 rounded-full font-rajdhani tracking-wide"
                      data-testid="button-place-order"
                    >
                      {isProcessingToyyibPay ? (
                        <>
                          <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                          Redirecting...
                        </>
                      ) : (
                        <>
                          <Wallet className="w-5 h-5 mr-2" />
                          Continue to ToyyibPay
                        </>
                      )}
                    </Button>
                  </div>
                ) : (
                  <div className="space-y-4">
                    <p className="text-gray-400 text-sm text-center py-8">
                      You will be redirected to Billplz to complete your payment
                    </p>
                    <Button
                      onClick={handleCheckout}
                      disabled={cartItems.length === 0 || isProcessingBillplz}
                      className="w-full bg-neon-yellow hover:bg-neon-yellow/90 text-black font-bold uppercase text-base h-12 rounded-full font-rajdhani tracking-wide"
                      data-testid="button-place-order-billplz"
                    >
                      {isProcessingBillplz ? (
                        <>
                          <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                          Redirecting...
                        </>
                      ) : (
                        <>
                          <CreditCard className="w-5 h-5 mr-2" />
                          Continue to Billplz
                        </>
                      )}
                    </Button>
                  </div>
                )}

                <p className="text-xs text-gray-400 text-center mt-6">
                  By completing this purchase, you agree to our terms and conditions.
                </p>
              </CardContent>
            </Card>
          </div>

          {/* Right Column - Order Summary */}
          <div className="lg:col-span-1">
            <Card className="bg-gradient-to-br from-[#1a1a1a] to-[#000000] border-2 border-neon-yellow/30 rounded-3xl sticky top-4">
              <CardHeader>
                <CardTitle className="text-2xl font-bebas text-white uppercase tracking-wider">
                  Order Summary
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-6">
                {/* Cart Items */}
                <div className="space-y-3">
                  {cartItems.map((item) => (
                    <div
                      key={item.id}
                      className="flex items-center gap-3 p-3 bg-[#000000]/50 rounded-xl border border-white/5"
                      data-testid={`checkout-item-${item.id}`}
                    >
                      <div className="flex-1">
                        <h3 className="font-rajdhani font-bold text-white text-sm">
                          {item.package.name}
                        </h3>
                        <p className="text-xs text-gray-400">
                          {item.package.aecoinAmount.toLocaleString()} AECOIN Ã— {item.quantity}
                        </p>
                      </div>
                      <div className="text-right">
                        <p className="font-bold text-neon-yellow text-sm">
                          RM{(parseFloat(item.package.price) * item.quantity).toFixed(0)}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>

                <Separator className="bg-white/10" />

                {/* Coupon Code */}
                <div>
                  <label className="block text-sm font-rajdhani font-semibold text-white mb-2">
                    <Tag className="w-4 h-4 inline mr-2" />
                    Coupon Code
                  </label>
                  {appliedCoupon ? (
                    <div className="flex items-center gap-2 p-3 bg-green-500/10 border border-green-500/30 rounded-2xl">
                      <Badge className="bg-green-500 text-black font-bold">
                        {appliedCoupon.code}
                      </Badge>
                      <span className="text-sm text-green-400 flex-1">
                        {appliedCoupon.discountType === 'percentage' 
                          ? `${appliedCoupon.discountValue}% OFF` 
                          : `RM${appliedCoupon.discountValue} OFF`}
                      </span>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={handleRemoveCoupon}
                        className="text-red-400 hover:text-red-300 h-7"
                        data-testid="button-remove-coupon"
                      >
                        Remove
                      </Button>
                    </div>
                  ) : (
                    <div className="flex gap-2">
                      <Input
                        value={couponCode}
                        onChange={(e) => setCouponCode(e.target.value.toUpperCase())}
                        placeholder="Enter code"
                        className="bg-[#000000] border-white/10 text-white rounded-xl"
                        data-testid="input-coupon-code"
                      />
                      <Button
                        onClick={handleApplyCoupon}
                        disabled={!couponCode.trim() || validateCoupon.isPending}
                        className="bg-white/10 hover:bg-white/20 text-white rounded-xl"
                        data-testid="button-apply-coupon"
                      >
                        {validateCoupon.isPending ? (
                          <Loader2 className="w-4 h-4 animate-spin" />
                        ) : (
                          "Apply"
                        )}
                      </Button>
                    </div>
                  )}
                </div>

                <Separator className="bg-white/10" />

                {/* Price Breakdown */}
                <div className="space-y-3">
                  <div className="flex justify-between text-white">
                    <span className="font-rajdhani">Subtotal:</span>
                    <span data-testid="text-subtotal">RM{subtotal.toFixed(0)}</span>
                  </div>
                  {appliedCoupon && (
                    <div className="flex justify-between text-green-400">
                      <span className="font-rajdhani">
                        Discount{appliedCoupon.discountType === 'percentage' ? ` (${appliedCoupon.discountValue}%)` : ''}:
                      </span>
                      <span data-testid="text-discount">-RM{discountAmount.toFixed(0)}</span>
                    </div>
                  )}
                  <Separator className="bg-white/10" />
                  <div className="flex justify-between text-xl font-bold">
                    <span className="text-white font-rajdhani">Total:</span>
                    <span className="text-neon-yellow" data-testid="text-total">
                      RM{total.toFixed(0)}
                    </span>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

