import { GallerySection } from "../GallerySection";
import car1Image from "@assets/stock_images/gta_5_sports_car_mus_ecdea8c8.jpg";
import car2Image from "@assets/stock_images/gta_5_sports_car_mus_07d7cbe9.jpg";
